import 'prismjs/themes/prism.css';
export default {
  name: 'MyPrism',
  data: function data() {
    return {
      html: this.$props.code
    };
  },
  props: {
    language: {
      type: String,
      default: 'php'
    },
    plugins: {
      type: Array,
      default: 'plugins'
    },
    code: {
      //type: String,
      default: ''
    }
  },
  mounted: function mounted() {
    if (this.$slots.default) {
      this.html = this.$slots.default[0].text;
    }
  }
};
