 .drag-box{
     display: flex;
     user-select: none;
 }
 .drag-box-item {
     flex: 1;
     background-color: #eff1f5;
     margin-right: 16px;
     border-radius: 6px;
     border: 1px #e1e4e8 solid;
 }
 .item-title{
     padding: 8px 8px 8px 12px;
     font-size: 14px;
     line-height: 1.5;
     color: #24292e;
     font-weight: 600;
 }
 .item-ul{
     padding: 0 8px 8px;
     max-height: 1000px;
     overflow-y: scroll;
 }
 .item-ul::-webkit-scrollbar{
     width: 0;
 }

 .overflow-text {
     display: block;
     color: #606266;
     overflow: hidden;
     max-width: 250px;
     padding-left: 4px;
     text-overflow: ellipsis;
     transition: color .3s;
     white-space: nowrap;
     line-height: 30px;
 }

 .drag-list {
     border: 1px #e1e4e8 solid;
     padding: 10px;
     margin: 5px 0 10px;
     list-style: none;
     background-color: #fff;
     border-radius: 6px;
     cursor: pointer;
     -webkit-transition: border .3s ease-in;
     transition: border .3s ease-in;
 }
 .drag-list:hover {
     border: 1px solid #20a0ff;
 }
 .drag-title {
     font-weight: 400;
     line-height: 25px;
     margin: 10px 0;
     font-size: 22px;
     color: #1f2f3d;
 }
 .ghost-style{
     display: block;
     color: transparent;
     border-style: dashed
 }

